                <span>К сожелению прикрепленный файл исчез из хранилища.</span>
            </div>
            <DocumentOnlineViewing v-if="fileLoaded" :file="file"/>
        </div>
    </div>
</template>

<script>
import Loading from "../UI/Loading";
import DocumentOnlineViewing from "../UI/DocumentOnlineViewing";
export default {
    name: "ViewDocument",
    components: {
        DocumentOnlineViewing,
        Loading,
    },
    data() {
        return {
            isLoaded: false,
            fileLoaded: false,
            found: false,
            file: null,
            fileError: false,
        }
    },
    mounted() {
        this.load();
    },
    methods: {
        load() {
            let req = axios.post('/docs', {
                id: this.$route.params.id,
            });
            req.then(value => {
                if (value['data']['status']['code'] == 0) {
                    value = value['data']['document'];
                    this.found = true;
                    fetch(`/file/${value['id_avt']}`)
                        .then(res => {
                            if (res.status === 200)
                                res.blob().then(blob => {
                                    this.file = new File([blob], `file.${value['extension']}`,  { type: blob.type });
                                    this.fileLoaded = true;
                                });
                            else this.fileError = true;
                        })
                } else {
                    /* TODO Error */
                }
            });
            req.catch(reason => {});
            req.finally(() => {
                this.isLoaded = true;
            })
        },
    }
}
</script>

<style scoped lang="sass">
    .document-container
        flex: auto
        .document-body
            display: flex
    .not__found
        max-width: 400px
        min-width: 320px
        width: auto
        margin: 0 auto
        display: flex
        justify-content: center
        align-items: center
        flex-direction: column
